mapper.select:
SELECT A.FREE_BOR_IDX, A.FREE_BOR_TITLE, A.USER_NM, A.USER_ID, A.FREE_BOR_CONTENTS, A.FREE_BOR_VIEW_CNT, A.FREE_BOR_LIKE, ( SUBSTR(REG_DE, 0, 4)||'-'|| SUBSTR(REG_DE, 5, 2)||'-'||SUBSTR(REG_DE, 7, 2)) AS REG_DE, A.USE_YN, (SELECT COUNT(*) FROM FREE_BOARD_REPLY WHERE FREE_BOR_IDX = A.FREE_BOR_IDX) AS RECENT FROM( SELECT ROWNUM RNUM, S.* FROM( SELECT F.FREE_BOR_IDX, F.FREE_BOR_TITLE, F.USER_NM, F.USER_ID, F.FREE_BOR_CONTENTS, F.FREE_BOR_VIEW_CNT, (SELECT COUNT(FREE_BOR_IDX) FROM FREE_LIKE_BOARD WHERE 1=1 AND FREE_BOR_IDX = F.FREE_BOR_IDX) AS FREE_BOR_LIKE, F.REG_DE, F.USE_YN FROM FREE_BOARD F WHERE 1=1 AND USE_YN='Y'
AND(USER_NM LIKE '%' || #{search} || '%' OR FREE_BOR_TITLE LIKE '%' || #{search} || '%')
ORDER BY FREE_BOR_IDX DESC ) S ) A WHERE RNUM BETWEEN #{startRowNo} AND #{endRowNo}
/* com.co.kr.board.free.controller.FreeBoardController.freeBoardSelectList */

SELECT COUNT(FREE_BOR_IDX) FROM FREE_BOARD WHERE 1=1 AND USE_YN='Y'
AND(USER_NM LIKE '%' || #{search} || '%' OR FREE_BOR_TITLE LIKE '%' || #{search} || '%')
/* com.co.kr.board.free.controller.FreeBoardController.selectFreeBoardListCount */

SELECT F.FREE_BOR_IDX, F.FREE_BOR_TITLE, F.USER_NM, F.USER_ID, F.FREE_BOR_CONTENTS, F.FREE_BOR_VIEW_CNT, F.REG_DE, F.USE_YN FROM FREE_BOARD F WHERE 1=1
AND FREE_BOR_IDX = #{freeBorIdx}
/* com.co.kr.board.free.controller.FreeBoardController.freeBoardDetail */

SELECT F.FREE_BOR_IDX, F.FREE_BOR_TITLE, F.USER_NM, F.USER_ID, F.FREE_BOR_CONTENTS, F.FREE_BOR_VIEW_CNT, F.REG_DE, F.USE_YN FROM FREE_BOARD F WHERE 1=1
AND FREE_BOR_IDX = #{freeBorIdx}
/* com.co.kr.board.free.controller.FreeBoardController.freeBoardDetail */

mapper.update:
		
			UPDATE FREE_BOARD
				SET 
					FREE_BOR_VIEW_CNT = FREE_BOR_VIEW_CNT + 1
				WHERE
					FREE_BOR_IDX = #{freeBorIdx}
		
       /* com.co.kr.board.free.controller.FreeBoardController.freeBoardUpdateCnt */

mapper.select:
			SELECT  FREE_BOR_IDX,
					USER_ID,
					REG_DE
				FROM FREE_LIKE_BOARD
				WHERE 1=1
				 
                AND FREE_BOR_IDX = #{freeBorIdx}
                
                 
                AND USER_ID = #{userId}
                
       /* com.co.kr.board.free.controller.FreeBoardController.freeBoardLike */

mapper.update:
UPDATE FREE_BOARD 
		SET USE_YN ='N',
			UPDATE_ID = #{updateId},
			UPDATE_DE = TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')		 
	WHERE FREE_BOR_IDX = #{freeBorIdx}
	
	/*com.co.kr.board.free.dao.freeBoardListDelete*/

mapper.select:
SELECT COUNT(SEARCH_WORD) FROM REAL_TIME_QUERY WHERE 1=1

	AND SEARCH_WORD = #{value}


/*com.co.kr.board.free.dao.selectSearchCheck*/

mapper.update:

	UPDATE REAL_TIME_QUERY
		SET 
			SEARCH_COUNT = SEARCH_COUNT + 1,
			UPDATE_DE = TO_CHAR(SYSDATE, 'YYYY-MM-DD')
		WHERE SEARCH_WORD = #{value}


/*com.co.kr.board.free.dao.updateSearchWordCount*/
